import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const SpiderWeb = ({ data, onCenterClick }) => {
  const svgRef = useRef();

  useEffect(() => {
    if (!data.length) {
      console.log('Нет данных для отрисовки');
      return;
    }

    const svg = d3.select(svgRef.current);
    const width = svg.attr('width');
    const height = svg.attr('height');

    // Очистка предыдущего рендеринга
    svg.selectAll('*').remove();

    // Группировка данных по school_teaching
    const groupedData = d3.group(data, d => d.school_teaching);

    // Цветовая шкала для разных школ
    const colorScale = d3.scaleOrdinal(d3.schemeCategory10);

    // Отрисовка связей между центрами
    groupedData.forEach((group, key) => {
      if (group.length > 1) {
        for (let i = 0; i < group.length - 1; i++) {
          for (let j = i + 1; j < group.length; j++) {
            svg.append('line')
              .attr('x1', group[i].x)
              .attr('y1', group[i].y)
              .attr('x2', group[j].x)
              .attr('y2', group[j].y)
              .attr('stroke', colorScale(key))
              .attr('stroke-width', 2);
          }
        }
      }
    });

    // Отрисовка центров
    svg.selectAll('circle')
      .data(data)
      .enter()
      .append('circle')
      .attr('cx', d => d.x)
      .attr('cy', d => d.y)
      .attr('r', 20)
      .attr('fill', d => colorScale(d.school_teaching))
      .on('click', (event, d) => onCenterClick(d));

    // Добавление текста
    svg.selectAll('text')
      .data(data)
      .enter()
      .append('text')
      .attr('x', d => d.x)
      .attr('y', d => d.y + 30)
      .text(d => d.person_name)
      .attr('text-anchor', 'middle')
      .attr('fill', 'black');
  }, [data, onCenterClick]);

  return (
    <svg ref={svgRef} width="800" height="800" style={{ border: '1px solid #ccc' }} />
  );
};

export default SpiderWeb;
