import React, { useEffect, useRef, useState } from 'react';
import Sigma from 'sigma';
import { Graph } from 'graphology';
import forceAtlas2 from 'graphology-layout-forceatlas2';

const SpiderWeb = ({ data, onCenterClick, onCenterDoubleClick }) => {
  const containerRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });

  // Функция для обновления размеров контейнера
  const updateDimensions = () => {
    if (containerRef.current) {
      const { width, height } = containerRef.current.getBoundingClientRect();
      setDimensions({ width, height });
    }
  };

  useEffect(() => {
    // Обновляем размеры при монтировании компонента
    updateDimensions();

    // Обновляем размеры при изменении размеров окна
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  useEffect(() => {
    console.log('Данные для отрисовки:', data);
    if (!data.length || dimensions.width === 0 || dimensions.height === 0) return;

    // Создаем граф
    const graph = new Graph();

    // Добавляем узлы
    data.forEach((item) => {
      graph.addNode(item.id, {
        label: item.person_name,
        x: item.x || Math.random() * dimensions.width,
        y: item.y || Math.random() * dimensions.height,
        size: 10,
        color: getColorBySchool(item.school_teaching),
      });
    });

    // Добавляем ребра
    const schoolMap = {};
    data.forEach((item) => {
      if (!schoolMap[item.school_teaching]) {
        schoolMap[item.school_teaching] = [];
      }
      schoolMap[item.school_teaching].push(item.id);
    });

    Object.values(schoolMap).forEach((group) => {
      if (group.length > 1) {
        for (let i = 0; i < group.length - 1; i++) {
          for (let j = i + 1; j < group.length; j++) {
            graph.addEdge(group[i], group[j], {
              color: '#ccc',
            });
          }
        }
      }
    });

    // Инициализируем Sigma
    const sigmaInstance = new Sigma(graph, containerRef.current, {
      allowInvalidContainer: false, // Убедитесь, что контейнер имеет корректные размеры
    });

    // Применяем ForceAtlas2 layout
    const settings = forceAtlas2.inferSettings(graph);
    forceAtlas2.assign(graph, { settings, iterations: 50 });

    // Обработка клика на узел
    sigmaInstance.on('clickNode', (event) => {
      const nodeId = event.node;
      const node = data.find((item) => item.id === nodeId);
      if (node) onCenterClick(node);
    });

    // Обработка двойного клика на узел
    sigmaInstance.on('doubleClickNode', (event) => {
      event.preventSigmaDefault(); // Отключаем стандартное поведение зума
      const nodeId = event.node;
      const node = data.find((item) => item.id === nodeId);
      if (node) onCenterDoubleClick(node);
    });

    return () => {
      sigmaInstance.kill();
    };
  }, [data, onCenterClick, onCenterDoubleClick, dimensions]);

  const getColorBySchool = (school) => {
    const colors = ['#FF6F61', '#6B5B95', '#88B04B', '#F7CAC9', '#92A8D1'];
    const index = school?.length % colors.length || 0;
    return colors[index];
  };

  return (
    <div
      ref={containerRef}
      style={{
        width: '800px',
        height: '800px',
        position: 'relative',
      }}
    />
  );
};

export default SpiderWeb;
