import React from 'react';
import ReactDOM from 'react-dom';

const ModalPortal = ({ children }) => {
  const modalRoot = document.getElementById('modal-root');
  return ReactDOM.createPortal(children, modalRoot);
};

const CenterDetailsModal = ({ center, onClose }) => {
  if (!center) return null;

  return (
    <ModalPortal>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000,
      }}>
        <div style={{
          backgroundColor: '#fff',
          padding: '20px',
          borderRadius: '5px',
          width: '300px',
          maxHeight: '80vh',
          overflowY: 'auto',
          position: 'relative',
        }}>
          <button
            onClick={onClose}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              background: 'none',
              border: 'none',
              fontSize: '16px',
              cursor: 'pointer',
            }}
          >
            ×
          </button>

          <h3>Данные центра</h3>
          <p><strong>Имя персонажа:</strong> {center.person_name}</p>
          <p><strong>Школа обучения:</strong> {center.school_teaching}</p>
          <p><strong>Учитель:</strong> {center.influenced_by}</p>
          <p><strong>Последователи:</strong> {center.influenced}</p>
        </div>
      </div>
    </ModalPortal>
  );
};

export default React.memo(CenterDetailsModal);
